mapper.select:
with tmp_date as( select year as year , yyyymm as yyyymm , week as week_num , concat(year,case when date_part('QUARTER',to_date(yyyymm,'YYYYMM')::timestamp) <= 2 then 1 else 2 end) as half , concat(year,date_part('QUARTER',to_date(yyyymm,'YYYYMM')::timestamp)) as quarter , concat(yyyymm,week) as week -- ,concat(substring(a.yyyymm,1,4),'년',substring(a.yyyymm,5,6),'월') as date_nm from cmapap.tbagile_pos_date a group by 1,2,3,4,5,6 order by 1 )
select
a.menu1_cd as menu_cd,a.menu1_nm as menu_nm
b.menu2_cd as menu_cd, b.menu2_nm as menu_nm
c.menu3_cd as menu_cd, c.menu3_nm as menu_nm
from cmapap.tb_menu1 a, cmapap.tb_menu2 b, cmapap.tb_menu3 c
,( SELECT a.* FROM cmapap.tbagile_pos_menu_list a,tmp_date b where 1=1
and b.year between #{dateStart} and #{dateEnd}
and b.half between #{dateStart} and #{dateEnd}
and b.quarter between #{dateStart} and #{dateEnd}
and b.yyyymm between #{dateStart} and #{dateEnd}
and b.week between #{dateStart} and #{dateEnd}
and a.sale_mm = b.yyyymm and a.week = b.week_num::numeric
and admi_cd like #{areaCd} || '%'
) d
where 1=1 and a.menu1_cd = b.menu1_cd and b.menu2_cd = c.menu2_cd
and a.menu1_cd = #{menuCd}
and b.menu2_cd = #{menuCd}
AND c.menu3_cd = d.menu3_cd
group by 1,2 order by 1
